const LoginSignIn = () =>{

    const [data, setData] = useState([]);

    //variable created to store current info from user
    const [info, setInfo] = useState({
      //create object to input all 3 values
      //name of these feilds is same as that of name of input boxes cuz we want to store the values that we get in the same input box
      name: "",
      email: "",
      password: "",
    });
    //  console.log(info)

    //to input info from user
    const getData = (event) => {
      const { value, name } = event.target;
      setInfo((prevInfo) => ({
        ...prevInfo,
        [name]: value,
      }));
    };

    //function to store info we got from the user
    const addData = (e) => {
      e.preventDefault();
      console.log(info);

      const { name, email, password } = info;
      if (name === "") {
        alert("Username is required");
      } else if (email === "") {
        alert("Email id is required");
      } else if (!email.includes("@")) {
        alert("Please enter valid Email ID");
      } else if (password === "") {
        alert("Password is required");
      } else if (password.length < 5) {
        alert("Password length should be greater than 5");
      } else {
        console.log("data added successfully");

        //localtroage.setItem(key : "" , value ; "")
        localStorage.setItem("useryoutube", JSON.stringify([...data, info]));
      }
    };

return(
    <div className="loginContainer">
    <h2 className="loginHeading"> SIGN UP </h2>
    <div className="LoginPage">

      <div>
        {/* <label htmlFor="email"> Email </label> */}
        <input
          className="loginUsername"
          name="email"
          type="email"
          placeholder="email"
          // value={info}
          onChange={getData}
        />
      </div>
      <div>
        {/* <label htmlFor="password"> Password </label> */}
        <input
          type="password"
          placeholder="Password"
          className="loginUsername"
          name="password"
          // value={info}
          onChange={getData}
        />
      </div>

      <button className="buttonlogin" onClick={addData}>
        Submit
      </button>

    </div>
  </div>
)
}

export default LoginSignIn;
