import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();

  const LoginPage = () => {
      localStorage.setItem("login", true);
      navigate("/");
  };


  useEffect(() => {
    const login = localStorage.getItem("login");

    if (!login) {
      // Redirect to the login page using Navigate when user is not logged in
      navigate("/");
    }
  }, []);

  return (
    <div className="LoginPage">
      <h2 className="loginHeading"> Please Login to access this page </h2>
      <input type="text" placeholder="Username" className="loginUsername" />
      <input type="text" placeholder="Password" className="loginUsername" />
      <button className="buttonlogin" onClick={LoginPage}>
        Submit
      </button>
    </div>
  );
};

export default Login;
