import { MAIN_LOGO } from "../utlis/constant.js";
import { useState } from "react";
import { Link } from "react-router-dom";

const Header = () => {
  //state variable for storing login button value
  const [loginbtn, setloginbtn] = useState("Login");

const LogInOutBtn = () => {
  //conditional operator, if loginbtn= login set it to logout, else set it to login
  loginbtn === "Login"
    ? setloginbtn("Logout")
    : setloginbtn("Login");
} 
  return (
    <div className="header">
      <div className="image-container">
        <a href="/">
          <img className="logoimg" alt="logo" src={MAIN_LOGO}></img>
        </a>
      </div>
      <div className="nav-items">
        <ul>
          <li>
            <Link to="/" className="pagebutton">Home </Link>
          </li>
          <li>
            <Link to="/About" className="pagebutton">About </Link>
           {/* <a href="/About"> About </a>  */}
          </li>
          <li>
            <Link to="/Contact" className="pagebutton">Contact </Link>
          </li>
          <li>Cart</li>
          <button
            className="login-button"
            onClick={LogInOutBtn}
          >
            {" "}
            {loginbtn}
          </button>
        </ul>
      </div>
    </div>
  );
};

export default Header;
