import { useEffect, useState } from "react";
import { json, useNavigate } from "react-router-dom";

const Login = () => {
  // const LoginPage = () => {
  //   localStorage.setItem("login", true);
  //   navigate("/");
  // };
  // const navigate = useNavigate();
  // useEffect(() => {
  //   const login = localStorage.getItem("login");

  //   if (login) {
  //     // Redirect to the login page using Navigate when user is not logged in
  //     navigate("/");
  //   }
  // }, []);

  // const [password, setPassword] = useState("");

  const [info, setInfo] = useState({
    //create object to input all 3 values
    //name of these feilds is same as that of name of input boxes cuz we want to store the values that we get in the same input box
    name: "",
    email: "",
    password: "",
  });
  //  console.log(info)

  const getData = (event) => {
    const { value, name } = event.target;
    setInfo((prevInfo) => ({
      ...prevInfo,
      [name]: value,
    }));
  };

  const addData = (e) => {
    e.preventDefault();
    console.log(info);

    const { name, email, password } = info;
    if (name === "") {
      alert("Username is required");
    } else if (email === "") {
      alert("Email id is required");
    } else if (!email.includes("@")) {
      alert("Please enter valid Email ID");
    } else if (password === "") {
      alert("Password is required");
    } else if (password.length < 5) {
      alert("Password length should be greater than 5");
    }
    else{
      console.log("data added successfully")


               //localtroage.setItem(key : "" , value ; "")
      localStorage.setItem("useryoutube",  JSON.stringify(info))
    }
  };
  return (
    <div className="loginContainer">
      <h2 className="loginHeading"> LOGIN </h2>
      <div className="LoginPage">
        <div>
          {/* <label htmlFor="name"> name</label> */}
          <input
            className="loginUsername"
            name="name"
            type="text"
            placeholder="Username"
            // value={info}
            onChange={getData}
          />
        </div>

        <div>
          {/* <label htmlFor="email"> Email </label> */}
          <input
            className="loginUsername"
            name="email"
            type="email"
            placeholder="email"
            // value={info}
            onChange={getData}
          />
        </div>
        <div>
          {/* <label htmlFor="password"> Password </label> */}
          <input
            type="password"
            placeholder="Password"
            className="loginUsername"
            name="password"
            // value={info}
            onChange={getData}
          />
        </div>

        <button className="buttonlogin" onClick={addData}>
          Submit
        </button>
      </div>
    </div>
  );
};

export default Login;
